import { Component, OnInit, OnDestroy } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { WebsocketService, Order } from '../../services/websocket.service';
import { Subscription } from 'rxjs';

interface ColumnConfig {
  key: string;
  label: string;
  visible: boolean;
}

interface Stats {
  totalOrders: number;
  totalValue: number;
  buyOrders: number;
  sellOrders: number;
  filledOrders: number;
  rejectedOrders: number;
  avgOrderSize: number;
  fillRate: number;
}

type SortColumn = 'timestamp' | 'clOrdID' | 'symbol' | 'price' | 'quantity' | 'value' | 'ordStatus';
type TimeFilter = 'all' | '5min' | '1hour' | 'today';

@Component({
  selector: 'app-order-grid',
  standalone: true,
  imports: [CommonModule, FormsModule],
  templateUrl: './order-grid.component.html',
  styleUrls: ['./order-grid.component.css']
})
export class OrderGridComponent implements OnInit, OnDestroy {
  orders: Order[] = [];
  filteredOrders: Order[] = [];
  connected = false;
  
  // Filtering & Search
  searchTerm = '';
  sideFilter: 'all' | 'buy' | 'sell' = 'all';
  statusFilter: 'all' | 'NEW' | 'FILLED' | 'PARTIALLY_FILLED' | 'REJECTED' = 'all';
  timeFilter: TimeFilter = 'all';
  
  // Sorting
  sortColumn: SortColumn = 'timestamp';
  sortDirection: 'asc' | 'desc' = 'desc';
  
  // Column Visibility
  columns: ColumnConfig[] = [
    { key: 'timestamp', label: 'Time', visible: true },
    { key: 'clOrdID', label: 'Order ID', visible: true },
    { key: 'symbol', label: 'Symbol', visible: true },
    { key: 'side', label: 'Side', visible: true },
    { key: 'quantity', label: 'Quantity', visible: true },
    { key: 'cumQty', label: 'Net Qty', visible: true },
    { key: 'price', label: 'Price', visible: true },
    { key: 'value', label: 'Value', visible: true },
    { key: 'ordStatus', label: 'Status', visible: true }
  ];
  
  // UI State
  showStats = true;
  showFilters = false;
  showColumnConfig = false;
  theme: 'dark' | 'light' = 'dark';
  autoScroll = true;
  soundEnabled = true;
  
  // Statistics
  stats: Stats = {
    totalOrders: 0,
    totalValue: 0,
    buyOrders: 0,
    sellOrders: 0,
    filledOrders: 0,
    rejectedOrders: 0,
    avgOrderSize: 0,
    fillRate: 0
  };
  
  private subscription?: Subscription;
  private connectionSubscription?: Subscription;
  private audio?: HTMLAudioElement;

  constructor(private wsService: WebsocketService) {
    // Load preferences from localStorage
    this.loadPreferences();
  }

  ngOnInit(): void {
    // Subscribe to incoming orders
    this.subscription = this.wsService.messages$.subscribe((order) => {
      // Add timestamp if not present
      if (!order.timestamp) {
        order.timestamp = Date.now();
      }
      
      // Add new order to the top of the list with animation class
      this.orders.unshift({ ...order, isNew: true });
      
      // Play sound for rejected orders
      if (order.ordStatus === 'REJECTED' && this.soundEnabled) {
        this.playAlert();
      }
      
      // Show browser notification for rejected orders
      if (order.ordStatus === 'REJECTED') {
        this.showNotification('Order Rejected', `Order ${order.clOrdID} for ${order.symbol} was rejected`);
      }
      
      // Remove animation class after animation completes
      setTimeout(() => {
        const orderIndex = this.orders.findIndex(o => o.clOrdID === order.clOrdID);
        if (orderIndex !== -1 && this.orders[orderIndex]) {
          this.orders[orderIndex].isNew = false;
        }
      }, 500);

      // Limit to 1000 orders to prevent memory issues
      if (this.orders.length > 1000) {
        this.orders = this.orders.slice(0, 1000);
      }
      
      // Update filtered orders and stats
      this.applyFilters();
      this.updateStats();
    });

    // Subscribe to connection status
    this.connectionSubscription = this.wsService.connected$.subscribe((status) => {
      this.connected = status;
    });
    
    // Request notification permission
    if ('Notification' in window && Notification.permission === 'default') {
      Notification.requestPermission();
    }
  }

  ngOnDestroy(): void {
    this.subscription?.unsubscribe();
    this.connectionSubscription?.unsubscribe();
  }
  
  // Filtering Methods
  applyFilters(): void {
    let filtered = [...this.orders];
    
    // Apply search filter
    if (this.searchTerm) {
      const term = this.searchTerm.toLowerCase();
      filtered = filtered.filter(order => 
        order.clOrdID.toLowerCase().includes(term) ||
        order.symbol.toLowerCase().includes(term) ||
        order.ordStatus.toLowerCase().includes(term)
      );
    }
    
    // Apply side filter
    if (this.sideFilter !== 'all') {
      filtered = filtered.filter(order => 
        this.sideFilter === 'buy' ? order.side === '1' : order.side === '2'
      );
    }
    
    // Apply status filter
    if (this.statusFilter !== 'all') {
      filtered = filtered.filter(order => order.ordStatus === this.statusFilter);
    }
    
    // Apply time filter
    if (this.timeFilter !== 'all') {
      const now = Date.now();
      const timeThresholds: Record<TimeFilter, number> = {
        'all': 0,
        '5min': 5 * 60 * 1000,
        '1hour': 60 * 60 * 1000,
        'today': now - new Date().setHours(0, 0, 0, 0)
      };
      
      const threshold = timeThresholds[this.timeFilter];
      if (threshold > 0) {
        filtered = filtered.filter(order => 
          order.timestamp && (now - order.timestamp) <= threshold
        );
      }
    }
    
    // Apply sorting
    filtered.sort((a, b) => {
      let aVal: any, bVal: any;
      
      switch (this.sortColumn) {
        case 'timestamp':
          aVal = a.timestamp || 0;
          bVal = b.timestamp || 0;
          break;
        case 'clOrdID':
          aVal = a.clOrdID;
          bVal = b.clOrdID;
          break;
        case 'symbol':
          aVal = a.symbol;
          bVal = b.symbol;
          break;
        case 'price':
          aVal = a.price;
          bVal = b.price;
          break;
        case 'quantity':
          aVal = a.quantity;
          bVal = b.quantity;
          break;
        case 'value':
          aVal = a.price * a.quantity;
          bVal = b.price * b.quantity;
          break;
        case 'ordStatus':
          aVal = a.ordStatus;
          bVal = b.ordStatus;
          break;
        default:
          aVal = a.timestamp || 0;
          bVal = b.timestamp || 0;
      }
      
      const comparison = aVal < bVal ? -1 : aVal > bVal ? 1 : 0;
      return this.sortDirection === 'asc' ? comparison : -comparison;
    });
    
    this.filteredOrders = filtered;
  }
  
  onSearchChange(): void {
    this.applyFilters();
  }
  
  setSideFilter(side: 'all' | 'buy' | 'sell'): void {
    this.sideFilter = side;
    this.applyFilters();
  }
  
  setStatusFilter(status: 'all' | 'NEW' | 'FILLED' | 'PARTIALLY_FILLED' | 'REJECTED'): void {
    this.statusFilter = status;
    this.applyFilters();
  }
  
  setTimeFilter(filter: TimeFilter): void {
    this.timeFilter = filter;
    this.applyFilters();
  }
  
  // Sorting Methods
  sort(column: SortColumn): void {
    if (this.sortColumn === column) {
      this.sortDirection = this.sortDirection === 'asc' ? 'desc' : 'asc';
    } else {
      this.sortColumn = column;
      this.sortDirection = 'desc';
    }
    this.applyFilters();
  }
  
  // Statistics Methods
  updateStats(): void {
    const totalOrders = this.orders.length;
    const buyOrders = this.orders.filter(o => o.side === '1').length;
    const sellOrders = this.orders.filter(o => o.side === '2').length;
    const filledOrders = this.orders.filter(o => o.ordStatus === 'FILLED').length;
    const rejectedOrders = this.orders.filter(o => o.ordStatus === 'REJECTED').length;
    const totalValue = this.orders.reduce((sum, o) => sum + (o.price * o.quantity), 0);
    const avgOrderSize = totalOrders > 0 ? this.orders.reduce((sum, o) => sum + o.quantity, 0) / totalOrders : 0;
    const fillRate = totalOrders > 0 ? (filledOrders / totalOrders) * 100 : 0;
    
    this.stats = {
      totalOrders,
      totalValue,
      buyOrders,
      sellOrders,
      filledOrders,
      rejectedOrders,
      avgOrderSize,
      fillRate
    };
  }
  
  // Export Methods
  exportToCSV(): void {
    const headers = this.columns
      .filter(col => col.visible && col.key !== 'side')
      .map(col => col.label);
    
    const rows = this.filteredOrders.map(order => {
      const row: string[] = [];
      this.columns.forEach(col => {
        if (!col.visible) return;
        
        switch (col.key) {
          case 'timestamp':
            row.push(order.timestamp ? new Date(order.timestamp).toLocaleString() : '');
            break;
          case 'clOrdID':
            row.push(order.clOrdID);
            break;
          case 'symbol':
            row.push(order.symbol);
            break;
          case 'side':
            row.push(this.getSideLabel(order.side));
            break;
          case 'quantity':
            row.push(order.quantity.toString());
            break;
          case 'cumQty':
            row.push(order.cumQty.toString());
            break;
          case 'price':
            row.push(order.price.toString());
            break;
          case 'value':
            row.push((order.price * order.quantity).toString());
            break;
          case 'ordStatus':
            row.push(order.ordStatus);
            break;
        }
      });
      return row;
    });
    
    const csvContent = [
      headers.join(','),
      ...rows.map(row => row.map(cell => `"${cell}"`).join(','))
    ].join('\n');
    
    const blob = new Blob([csvContent], { type: 'text/csv;charset=utf-8;' });
    const link = document.createElement('a');
    const url = URL.createObjectURL(blob);
    
    link.setAttribute('href', url);
    link.setAttribute('download', `orders_${new Date().toISOString()}.csv`);
    link.style.visibility = 'hidden';
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  }
  
  // Column Visibility Methods
  toggleColumn(columnKey: string): void {
    const column = this.columns.find(col => col.key === columnKey);
    if (column) {
      column.visible = !column.visible;
      this.savePreferences();
    }
  }
  
  // Theme Methods
  toggleTheme(): void {
    this.theme = this.theme === 'dark' ? 'light' : 'dark';
    document.body.classList.toggle('light-theme', this.theme === 'light');
    this.savePreferences();
  }
  
  // Notification Methods
  showNotification(title: string, body: string): void {
    if ('Notification' in window && Notification.permission === 'granted') {
      new Notification(title, { body, icon: '/favicon.ico' });
    }
  }
  
  playAlert(): void {
    // Create a simple beep using Web Audio API
    try {
      const audioContext = new (window.AudioContext || (window as any).webkitAudioContext)();
      const oscillator = audioContext.createOscillator();
      const gainNode = audioContext.createGain();
      
      oscillator.connect(gainNode);
      gainNode.connect(audioContext.destination);
      
      oscillator.frequency.value = 800;
      oscillator.type = 'sine';
      
      gainNode.gain.setValueAtTime(0.3, audioContext.currentTime);
      gainNode.gain.exponentialRampToValueAtTime(0.01, audioContext.currentTime + 0.5);
      
      oscillator.start(audioContext.currentTime);
      oscillator.stop(audioContext.currentTime + 0.5);
    } catch (e) {
      console.error('Audio playback failed:', e);
    }
  }
  
  toggleSound(): void {
    this.soundEnabled = !this.soundEnabled;
    this.savePreferences();
  }
  
  // Preference Methods
  savePreferences(): void {
    const prefs = {
      columns: this.columns,
      theme: this.theme,
      showStats: this.showStats,
      soundEnabled: this.soundEnabled
    };
    localStorage.setItem('orderGridPreferences', JSON.stringify(prefs));
  }
  
  loadPreferences(): void {
    const saved = localStorage.getItem('orderGridPreferences');
    if (saved) {
      try {
        const prefs = JSON.parse(saved);
        if (prefs.columns) this.columns = prefs.columns;
        if (prefs.theme) {
          this.theme = prefs.theme;
          document.body.classList.toggle('light-theme', this.theme === 'light');
        }
        if (prefs.showStats !== undefined) this.showStats = prefs.showStats;
        if (prefs.soundEnabled !== undefined) this.soundEnabled = prefs.soundEnabled;
      } catch (e) {
        console.error('Failed to load preferences:', e);
      }
    }
  }

  getSideClass(side: string): string {
    return side === '1' ? 'buy' : 'sell';
  }

  getSideLabel(side: string): string {
    return side === '1' ? 'BUY' : 'SELL';
  }

  formatPrice(price: number): string {
    return price.toFixed(2);
  }

  formatQuantity(qty: number): string {
    return qty.toLocaleString();
  }
  
  formatTimestamp(timestamp: number | undefined): string {
    if (!timestamp) return '';
    const date = new Date(timestamp);
    return date.toLocaleTimeString();
  }
  
  formatDate(timestamp: number | undefined): string {
    if (!timestamp) return '';
    const date = new Date(timestamp);
    return date.toLocaleDateString() + ' ' + date.toLocaleTimeString();
  }

  getStatusClass(status: string): string {
    switch(status) {
      case 'FILLED':
        return 'status-filled';
      case 'PARTIALLY_FILLED':
        return 'status-partial';
      case 'NEW':
        return 'status-new';
      case 'REJECTED':
        return 'status-rejected';
      default:
        return 'status-unknown';
    }
  }

  clearOrders(): void {
    this.orders = [];
    this.filteredOrders = [];
    this.updateStats();
  }

  trackByOrderId(index: number, order: Order): string {
    return order.clOrdID;
  }
  
  toggleStats(): void {
    this.showStats = !this.showStats;
    this.savePreferences();
  }
  
  toggleFilters(): void {
    this.showFilters = !this.showFilters;
  }
  
  toggleColumnConfig(): void {
    this.showColumnConfig = !this.showColumnConfig;
  }
  
  isColumnVisible(key: string): boolean {
    const column = this.columns.find(col => col.key === key);
    return column ? column.visible : true;
  }
}
<div class="order-grid-container">
  <div class="grid-header">
    <div class="title-section">
      <h2>Live Order Blotter</h2>
      <span class="order-count">{{ orders.length }} orders</span>
    </div>
    <button class="clear-btn" (click)="clearOrders()" *ngIf="orders.length > 0">
      <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2">
        <path d="M3 6h18M19 6v14a2 2 0 01-2 2H7a2 2 0 01-2-2V6m3 0V4a2 2 0 012-2h4a2 2 0 012 2v2"/>
      </svg>
      Clear
    </button>
  </div>

  <div class="table-container" *ngIf="orders.length > 0; else emptyState">
    <table class="order-table">
      <thead>
        <tr>
          <th>Order ID</th>
          <th>Symbol</th>
          <th>Side</th>
          <th>Quantity</th>
          <th>Net Qty</th>
          <th>Price</th>
          <th>Value</th>
          <th>Status</th>
        </tr>
      </thead>
      <tbody>
        <tr *ngFor="let order of orders; trackBy: trackByOrderId" 
            [class.new-order]="order.isNew"
            [class.buy-row]="getSideClass(order.side) === 'buy'"
            [class.sell-row]="getSideClass(order.side) === 'sell'">
          <td class="order-id">{{ order.clOrdID }}</td>
          <td class="symbol">{{ order.symbol }}</td>
          <td>
            <span class="side-badge" [class]="getSideClass(order.side)">
              {{ getSideLabel(order.side) }}
            </span>
          </td>
          <td class="numeric">{{ formatQuantity(order.quantity) }}</td>
          <td class="numeric cumqty" [class.positive]="order.cumQty > 0" [class.negative]="order.cumQty < 0">{{ formatQuantity(order.cumQty) }}</td>
          <td class="numeric price">${{ formatPrice(order.price) }}</td>
          <td class="numeric value">${{ formatPrice(order.price * order.quantity) }}</td>
          <td>
            <span class="status-badge" [class]="getStatusClass(order.ordStatus)">
              {{ order.ordStatus }}
            </span>
          </td>
        </tr>
      </tbody>
    </table>
  </div>

  <ng-template #emptyState>
    <div class="empty-state">
      <svg width="64" height="64" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="1.5">
        <path d="M9 5H7a2 2 0 00-2 2v12a2 2 0 002 2h10a2 2 0 002-2V7a2 2 0 00-2-2h-2M9 5a2 2 0 002 2h2a2 2 0 002-2M9 5a2 2 0 012-2h2a2 2 0 012 2"/>
        <path d="M9 12h6m-6 4h6"/>
      </svg>
      <h3>No orders yet</h3>
      <p>Orders will appear here as they are received from the FIX gateway</p>
      <div class="status-indicator" [class.connected]="connected">
        <span class="dot"></span>
        <span>{{ connected ? 'Connected to backend' : 'Connecting...' }}</span>
      </div>
    </div>
  </ng-template>
</div>
