      filtered = filtered.filter(order => order.clOrdID.toLowerCase().includes(term) || order.symbol.toLowerCase().includes(term) || order.ordStatus.toLowerCase().includes(term));
    }
    // Apply side filter
    if (this.sideFilter !== 'all') {
      filtered = filtered.filter(order => this.sideFilter === 'buy' ? order.side === '1' : order.side === '2');
    }
    // Apply status filter
    if (this.statusFilter !== 'all') {
      filtered = filtered.filter(order => order.ordStatus === this.statusFilter);
    }
    // Apply time filter
    if (this.timeFilter !== 'all') {
      const now = Date.now();
      const timeThresholds = {
