      }
    }
    // Apply sorting
    filtered.sort((a, b) => {
      let aVal, bVal;
      switch (this.sortColumn) {
        case 'timestamp':
          aVal = a.timestamp || 0;
          bVal = b.timestamp || 0;
          break;
        case 'clOrdID':
          aVal = a.clOrdID;
          bVal = b.clOrdID;
          break;
        case 'symbol':
